import React, { useState, useEffect } from 'react';
import avatar from '../images/avatar.png';

const CustomerControl = () => {
  const [id, setID] = useState('');
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [house_no, setHouse] = useState('');
  const [city, setCity] = useState('');
  const [zipcode, setZipCode] = useState('');
  const [username, setUsername] = useState('');
  const [MyAccounts, setAccounts] = useState([]);
  const [current_balance, setBalance] = useState('');
  const [Alltransaction, SetTransaction] = useState([]);

  const DeleteAccount = async (account_id) => {
    try {
      const query = await fetch(`http://localhost:5000/accounts/${account_id}`, {
        method: 'DELETE',
      });
      console.log(query);
    } catch (error) {
      console.log(error);
    }
    window.location.reload();
  };

  const AddAccount = async () => {
    const customer_id = document.getElementById('customer_id_value').value;
    const body = { customer_id, current_balance };
    const query = await fetch('http://localhost:5000/accounts', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(body),
    });
    window.location.reload();

    console.log(query);
  };

  const GetAccountDetails = async () => {
    try {
      const query = await fetch(`http://localhost:5000/accounts/${id}`);
      const data = await query.json();
      setAccounts(data);
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  const GetTransactions = async () => {
    try {
      const customer_id = document.getElementById('customer_id_value').value;
      const query = await fetch(`http://localhost:5000/transaction/${customer_id}`);
      const data = await query.json();
      SetTransaction(data);
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  const GetCustomer = async () => {
    try {
      const parameters = window.location.search.substring(1).split('&');
      const temp = parameters[0].split('=');
      console.log(parameters);
      console.log(temp);
      const username = temp[1];
      const query = await fetch(`http://localhost:5000/customer/${username}`);
      const data = await query.json();
      console.log(data);
      setID(data['customer_id']);
      setName(data['name']);
      setPhone(data['phone']);
      setEmail(data['email']);
      setHouse(data['house_no']);
      setCity(data['city']);
      setZipCode(data['zipcode']);
      setUsername(data['username']);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    GetCustomer();
    // eslint-disable-next-line
  }, []);

  return (
    <div>
      <div className="border p-5" style={{ backgroundColor: '#006d77' }}>
        <h1 className="mt-5" style={{ textAlign: 'center', color: '#e5e5e5' }}>
          Customer Details
        </h1>
        <hr style={{ color: '#FFFFFF' }}></hr>
        <img src={avatar} alt="missing avatar" className="rounded mx-auto d-block" />
        <h3 className="mt-5" style={{ textAlign: 'center', color: '#e5e5e5' }}>
          username@{username}
        </h3>
      </div>
      <div className="container border mt-5 p-5">
        <h2>Personal Details</h2>
        <div className="input-group mb-3 mt-3">
          <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon1">
              customerid@
            </span>
          </div>
          <input
            type="text"
            value={id}
            className="form-control"
            placeholder="id"
            aria-label="Username"
            aria-describedby="basic-addon1"
            readOnly
          />
        </div>
        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon1">
              name@
            </span>
          </div>
          <input
            type="text"
            value={name}
            className="form-control"
            placeholder="Name"
            aria-label="Username"
            aria-describedby="basic-addon1"
            readOnly
          />
        </div>
        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon1">
              phone@
            </span>
          </div>
          <input
            type="text"
            value={phone}
            className="form-control"
            placeholder="phone"
            aria-label="Username"
            aria-describedby="basic-addon1"
            readOnly
          />
        </div>
        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon1">
              email@
            </span>
          </div>
          <input
            type="text"
            value={email}
            className="form-control"
            placeholder="Email"
            aria-label="Username"
            aria-describedby="basic-addon1"
            readOnly
          />
        </div>
        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon1">
              house@
            </span>
          </div>
          <input
            type="text"
            value={house_no}
            className="form-control"
            placeholder="house"
            aria-label="Username"
            aria-describedby="basic-addon1"
            readOnly
          />
        </div>
        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon1">
              city@
            </span>
          </div>
          <input
            type="text"
            value={city}
            className="form-control"
            placeholder="City"
            aria-label="Username"
            aria-describedby="basic-addon1"
            readOnly
          />
        </div>
        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon1">
              zipcode@
            </span>
          </div>
          <input
            type="text"
            value={zipcode}
            className="form-control"
            placeholder="Zip Code"
            aria-label="Username"
            aria-describedby="basic-addon1"
            readOnly
          />
        </div>
        <hr></hr>
        <button
          className="btn btn-outline-info"
          data-toggle="collapse"
          data-target="#collapseExample"
          aria-expanded="false"
          aria-controls="collapseExample"
        >
          Create Account
        </button>

        <button
          className="btn btn-outline-primary ml-3"
          type="button"
          data-toggle="collapse"
          data-target="#AccountDetails"
          aria-expanded="false"
          aria-controls="collapseExample"
          onClick={GetAccountDetails}
        >
          Get Account Details
        </button>

        <button
          className="btn btn-success ml-3"
          onClick={GetTransactions}
          type="button"
          data-toggle="collapse"
          data-target="#alltransaction"
          aria-expanded="false"
          aria-controls="collapseExample"
        >
          View Transactions
        </button>

        <div className="collapse" id="collapseExample">
          <div className="form-group shadow p-3 mb-5 bg-white rounded mt-3">
            <label>Customer Id</label>
            <input
              type="text"
              value={id}
              className="form-control mb-3"
              id="customer_id_value"
              name="username"
              aria-describedby="emailHelp"
              disabled
              required
            />
            <label>Balance</label>
            <input
              type="text"
              className="form-control"
              id="exampleInputPassword1"
              onChange={(e) => setBalance(e.target.value)}
              required
            />

            <button className="btn btn-primary btn-lg mt-3" onClick={AddAccount}>
              Add
            </button>
          </div>
        </div>

        <div className="collapse" id="alltransaction">
          <div className="card card-body">
            Your Transactions
            <table className="table">
              <thead className="thead-light">
                <tr>
                  <th scope="col">#</th>
                  <th scope="col">Account ID</th>
                  <th scope="col">Action</th>
                  <th scope="col">Amount</th>
                  <th scope="col">Branch ID</th>
                  <th scope="col">Date of Transaction</th>
                </tr>
              </thead>
              <tbody>
                {Alltransaction.map((t) => (
                  <tr key={t.transaction_id}>
                    <td>{t.transaction_id}</td>
                    <td>{t.account_id}</td>
                    <td>{t.action}</td>
                    <td>{t.amount}</td>
                    <td>{t.branch_id}</td>
                    <td>{t.date_of_transaction}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {MyAccounts.map((account) => (
          <form
            action="http://localhost:3000/customer/transaction"
            method="GET"
            key={account.account_id}
          >
            <div className="card shadow-lg p-3 mb-5 bg-white rounded collapse" id="AccountDetails">
              <hr className="mt-5" />
              <h1 style={{ textAlign: 'left' }}>Savings Account</h1>
              <hr />
              <div className="card-header">Account #{MyAccounts.indexOf(account) + 1}</div>
              <div className="card-body">
                <div className="input-group mb-3">
                  <div className="input-group-prepend">
                    <span className="input-group-text" id="basic-addon3">
                      @account no
                    </span>
                  </div>
                  <input
                    type="text"
                    value={account.account_id}
                    name="account_no"
                    className="form-control"
                    aria-describedby="basic-addon3"
                    readOnly
                  />
                </div>

                <div className="input-group mb-3">
                  <div className="input-group-prepend">
                    <span className="input-group-text">â‚¹</span>
                  </div>
                  <input
                    type="text"
                    value={account.current_balance}
                    className="form-control"
                    aria-label="Amount (to the nearest dollar)"
                    readOnly
                  />
                </div>

                <div className="input-group mb-3">
                  <div className="input-group-prepend">
                    <span className="input-group-text">Date Opened</span>
                  </div>
                  <input
                    type="text"
                    value={account.date_opened.substring(0, 10) + ' ' + account.date_opened.substring(11, 16)}
                    className="form-control"
                    aria-label="Amount (to the nearest dollar)"
                    readOnly
                  />
                </div>
                <button className="btn btn-info" type="submit">
                  Transaction
                </button>
                <button
                  className="btn btn-danger ml-3"
                  type="button"
                  onClick={() => DeleteAccount(account.account_id)}
                >
                  Delete
                </button>
              </div>
            </div>
          </form>
        ))}
      </div>
    </div>
  );
};

export default CustomerControl;
