import React, { useState, useEffect } from 'react';
import avatar from '../images/avatar.png';

const CustomerControl = () => {
  const [id, setID] = useState('');
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [house_no, setHouse] = useState('');
  const [city, setCity] = useState('');
  const [zipcode, setZipCode] = useState('');
  const [username, setUsername] = useState('');
  const [MyAccounts, setAccounts] = useState([]);
  const [current_balance, setBalance] = useState('');
  const [Alltransaction, SetTransaction] = useState([]);

  const DeleteAccount = async (account_id) => {
    try {
      const query = await fetch(`http://localhost:5000/accounts/${account_id}`, {
        method: 'DELETE',
      });
      console.log(query);
    } catch (error) {
      console.log(error);
    }
    window.location.reload();
  };

  const AddAccount = async () => {
    const customer_id = document.getElementById('customer_id_value').value;
    const body = { customer_id, current_balance };
    const query = await fetch('http://localhost:5000/accounts', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(body),
    });
    window.location.reload();

    console.log(query);
  };

  const GetAccountDetails = async () => {
    try {
      const query = await fetch(`http://localhost:5000/accounts/${id}`);
      const data = await query.json();
      setAccounts(data);
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  const GetTransactions = async () => {
    try {
      const customer_id = document.getElementById('customer_id_value').value;
      const query = await fetch(`http://localhost:5000/transaction/${customer_id}`);
      const data = await query.json();
      SetTransaction(data);
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  const GetCustomer = async () => {
    try {
      const parameters = window.location.search.substring(1).split('&');
      const temp = parameters[0].split('=');
      const username = temp[1];
      const query = await fetch(`http://localhost:5000/customer/${username}`);
      const data = await query.json();
      setID(data['customer_id']);
      setName(data['name']);
      setPhone(data['phone']);
      setEmail(data['email']);
      setHouse(data['house_no']);
      setCity(data['city']);
      setZipCode(data['zipcode']);
      setUsername(data['username']);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    GetCustomer();
  }, []);

  return (
    <div>
      <div className="border p-5" style={{ backgroundColor: '#006d77' }}>
        <h1 className="mt-5" style={{ textAlign: 'center', color: '#e5e5e5' }}>
          Customer Details
        </h1>
        <hr style={{ color: '#FFFFFF' }}></hr>
        <img src={avatar} alt="missing avatar" className="rounded mx-auto d-block" />
        <h3 className="mt-5" style={{ textAlign: 'center', color: '#e5e5e5' }}>
          username@{username}
        </h3>
      </div>
      <div className="container border mt-5 p-5">
        <h2>Personal Details</h2>

        {/* id reste readonly car c’est un ID */}
        <div className="input-group mb-3 mt-3">
          <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon1">
              customerid@
            </span>
          </div>
          <input
            type="text"
            value={id}
            className="form-control"
            placeholder="id"
            aria-label="Username"
            aria-describedby="basic-addon1"
            readOnly
          />
        </div>

        {/* Tous les autres champs sont modifiables */}
        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon1">
              name@
            </span>
          </div>
          <input
            type="text"
            value={name}
            className="form-control"
            placeholder="Name"
            aria-label="Username"
            aria-describedby="basic-addon1"
            onChange={(e) => setName(e.target.value)}
          />
        </div>

        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon1">
              phone@
            </span>
          </div>
          <input
            type="text"
            value={phone}
            className="form-control"
            placeholder="phone"
            aria-label="Username"
            aria-describedby="basic-addon1"
            onChange={(e) => setPhone(e.target.value)}
          />
        </div>

        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon1">
              email@
            </span>
          </div>
          <input
            type="text"
            value={email}
            className="form-control"
            placeholder="Email"
            aria-label="Username"
            aria-describedby="basic-addon1"
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>

        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon1">
              house@
            </span>
          </div>
          <input
            type="text"
            value={house_no}
            className="form-control"
            placeholder="house"
            aria-label="Username"
            aria-describedby="basic-addon1"
            onChange={(e) => setHouse(e.target.value)}
          />
        </div>

        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon1">
              city@
            </span>
          </div>
          <input
            type="text"
            value={city}
            className="form-control"
            placeholder="City"
            aria-label="Username"
            aria-describedby="basic-addon1"
            onChange={(e) => setCity(e.target.value)}
          />
        </div>

        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon1">
              zipcode@
            </span>
          </div>
          <input
            type="text"
            value={zipcode}
            className="form-control"
            placeholder="Zip Code"
            aria-label="Username"
            aria-describedby="basic-addon1"
            onChange={(e) => setZipCode(e.target.value)}
          />
        </div>

        <hr></hr>

        {/* Les boutons et le reste restent inchangés */}

        <button
          className="btn btn-outline-info"
          data-toggle="collapse"
          data-target="#collapseExample"
          aria-expanded="false"
          aria-controls="collapseExample"
        >
          Create Account
        </button>

        <button
          className="btn btn-outline-primary ml-3"
          type="button"
          data-toggle="collapse"
          data-target="#AccountDetails"
          aria-expanded="false"
          aria-controls="collapseExample"
          onClick={GetAccountDetails}
        >
          Get Account Details
        </button>

        <button
          className="btn btn-success ml-3"
          onClick={GetTransactions}
          type="button"
          data-toggle="collapse"
          data-target="#alltransaction"
          aria-expanded="false"
          aria-controls="collapseExample"
        >
          View Transactions
        </button>

        <div className="collapse" id="collapseExample">
          <div className="form-group shadow p-3 mb-5 bg-white rounded mt-3">
            <label>Customer Id</label>
            <input
              type="text"
              value={id}
              className="form-control mb-3"
              id="customer_id_value"
              name="username"
              aria-describedby="emailHelp"
              disabled
              required
            />
            <label>Balance</label>
            <input
              type="text"
              className="form-control"
              id="exampleInputPassword1"
              onChange={(e) => setBalance(e.target.value)}
              required
            />

            <button className="btn btn-primary btn-lg mt-3" onClick={AddAccount}>
              Add
            </button>
          </div>
        </div>

        <div className="collapse" id="alltransaction">
          <div className="card card-body">
            Your Transactions
            <table className="table">
              <thead className="thead-light">
                <tr>
                  <th scope="col">#</th>
                  <th scope="col">Account ID</th>
                  <th scope="col">Action</th>
                  <th scope="col">Amount</th>
                  <th scope="col">Branch ID</th>
                  <th scope="col">Date of Transaction</th>
                </tr>
              </thead>
              <tbody>
                {Alltransaction.map((t) => (
                  <tr key={t.transaction_id}>
                    <td>{t.transaction_id}</td>
                    <td>{t.account_id}</td>
                    <td>{t.action}</td>
                    <td>{t.amount}</td>
                    <td>{t.branch_id}</td>
                    <td>{t.date_of_transaction}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {MyAccounts.map((account) => (
          <form
            action="http://localhost:3000/customer/transaction"
            method="GET"
            key={account.account_id}
          >
            <div className="card shadow-lg p-3 mb-5 bg-white rounded collapse" id="AccountDetails">
              <hr className="mt-5" />
              <h1 style={{ textAlign: 'left' }}>Savings Account</h1>
              <hr />
              <div className="card-header">Account #{MyAccounts.indexOf(account) + 1}</div>
              <ul className="list-group list-group-flush">
                <li className="list-group-item">
                  Account ID
                  <input
                    type="text"
                    id="account_id"
                    name="account_id"
                    value={account.account_id}
                    disabled
                  />
                </li>
                <li className="list-group-item">
                  Customer ID
                  <input type="text" id="customer_id" name="customer_id" value={id} disabled />
                </li>
                <li className="list-group-item">
                  Current Balance
                  <input
                    type="text"
                    id="current_balance"
                    name="current_balance"
                    value={account.current_balance}
                    disabled
                  />
                </li>
                <li className="list-group-item">
                  Branch ID
                  <input type="text" id="branch_id" name="branch_id" value={account.branch_id} disabled />
                </li>
                <li className="list-group-item">
                  Date Created
                  <input
                    type="text"
                    id="date_created"
                    name="date_created"
                    value={account.date_created}
                    disabled
                  />
                </li>
                <li className="list-group-item">
                  <button
                    className="btn btn-danger mt-3"
                    onClick={(e) => {
                      e.preventDefault();
                      DeleteAccount(account.account_id);
                    }}
                  >
                    Delete Account
                  </button>
                </li>
              </ul>
            </div>
          </form>
        ))}
      </div>
    </div>
  );
};

export default CustomerControl;
